INSERT INTO "en_us" ("ID","항목","TEXTID","COLOR","DECORATION","표정","SOUND","TEXT","DESCRIPTION") VALUES (90401,'파츠설명','BACKPACK_PBS000',NULL,NULL,'UI',NULL,'Ideal for beginners, this booster is simple to control because of its lack of real power.','핀켓백커');
INSERT INTO "en_us" ("ID","항목","TEXTID","COLOR","DECORATION","표정","SOUND","TEXT","DESCRIPTION") VALUES (90402,'파츠설명','BACKPACK_PBS003',NULL,NULL,'UI',NULL,'A high-performance booster offering the best explosive output power available on the planet.  Nothing can outrun a Sonic Boom pack.','소닉부머');
INSERT INTO "en_us" ("ID","항목","TEXTID","COLOR","DECORATION","표정","SOUND","TEXT","DESCRIPTION") VALUES (90403,'파츠설명','BACKPACK_PBM002',NULL,NULL,'UI',NULL,'This general use booster is faster and more powerful than the P-Booster.  With more power comes less control.','부스터');
INSERT INTO "en_us" ("ID","항목","TEXTID","COLOR","DECORATION","표정","SOUND","TEXT","DESCRIPTION") VALUES (90404,'파츠설명','BACKPACK_PRC008',NULL,NULL,'UI',NULL,'The greatest advantage of this device is that it allows it''s pilot to fire missiles while moving.','토마호크-S');
INSERT INTO "en_us" ("ID","항목","TEXTID","COLOR","DECORATION","표정","SOUND","TEXT","DESCRIPTION") VALUES (90405,'파츠설명','BACKPACK_PSD004',NULL,NULL,'UI',NULL,'A highly customizable contraption that, if properly handled, can help you achieve record-breaking speeds.','센티넬');
INSERT INTO "en_us" ("ID","항목","TEXTID","COLOR","DECORATION","표정","SOUND","TEXT","DESCRIPTION") VALUES (90406,'파츠설명','BACKPACK_PBS004',NULL,NULL,'UI',NULL,'This pack has a built-in energy generator, allowing a Mechanaught to boost effectively without relying on its core EN Output.','디스펜서');
INSERT INTO "en_us" ("ID","항목","TEXTID","COLOR","DECORATION","표정","SOUND","TEXT","DESCRIPTION") VALUES (90407,'파츠설명','BACKPACK_PRC012',NULL,NULL,'UI',NULL,'A Mechanaught equipped with this pack can single-handedly target a large area of a battle site, even from the rear. The only drawback is that you must be stationary in order to fire the weapon.','미사일런처');
INSERT INTO "en_us" ("ID","항목","TEXTID","COLOR","DECORATION","표정","SOUND","TEXT","DESCRIPTION") VALUES (90408,'파츠설명','BACKPACK_GM',NULL,NULL,'UI',NULL,'GM booster. When using GM Mechanaught, please equip it with GM Mechanaught head, core, arms, legs, and booster.',NULL);
INSERT INTO "en_us" ("ID","항목","TEXTID","COLOR","DECORATION","표정","SOUND","TEXT","DESCRIPTION") VALUES (90409,'파츠설명','BACKPACK_PRC009',NULL,NULL,'UI',NULL,'Developed by NEO, a giant in the defense industry of the Palamo Federation, this pack features a relatively high mobility rating and a top-secret diversified attack mechanism.',NULL);
INSERT INTO "en_us" ("ID","항목","TEXTID","COLOR","DECORATION","표정","SOUND","TEXT","DESCRIPTION") VALUES (90410,'파츠설명','BACKPACK_PSD005',NULL,NULL,'UI',NULL,'Instant high-powered output that creates amazing booster jumps on the battlefield.  The only drawback is its high energy drain.',NULL);
INSERT INTO "en_us" ("ID","항목","TEXTID","COLOR","DECORATION","표정","SOUND","TEXT","DESCRIPTION") VALUES (90501,'유닛설명','Unit_UnitSlotA_1',NULL,NULL,'UI',NULL,'As the colonial war spread across the planet, mobility became increasingly important. The invention of the speed-focused Rushnik sparked the "Speed Race." $nNote: The booster and weapons are sold separately.',NULL);
INSERT INTO "en_us" ("ID","항목","TEXTID","COLOR","DECORATION","표정","SOUND","TEXT","DESCRIPTION") VALUES (90502,'유닛설명','Unit_UnitSlotB_1',NULL,NULL,'UI',NULL,'The Mekhi model features defensive improvements while retaining good overall speed. Ideal for small battles, this model is a favorite among veteran pilots. $nNote: The booster and weapons are sold separately.',NULL);
INSERT INTO "en_us" ("ID","항목","TEXTID","COLOR","DECORATION","표정","SOUND","TEXT","DESCRIPTION") VALUES (90503,'유닛설명','Unit_UnitSlotC_1',NULL,NULL,'UI',NULL,'The Trooper''s balance of defense and mobility has become the standard for Mechanaught design. Able to respond to a variety of attacks, it is suited for almost any battle environment. $nNote: The booster and weapons are sold separately.',NULL);
INSERT INTO "en_us" ("ID","항목","TEXTID","COLOR","DECORATION","표정","SOUND","TEXT","DESCRIPTION") VALUES (90504,'유닛설명','Unit_UnitSlotD_1',NULL,NULL,'UI',NULL,'The aptly named Frontliner is built for use on the frontlines of the battlefield. The Frontliner''s design offers better defenses than a general Mechanaught.  $nNote: The booster and weapons are sold separately.',NULL);
INSERT INTO "en_us" ("ID","항목","TEXTID","COLOR","DECORATION","표정","SOUND","TEXT","DESCRIPTION") VALUES (90505,'유닛설명','Unit_UnitSlotE_1',NULL,NULL,'UI',NULL,'The stalwart Boomrocker was built to withstand a frontline assault or provide fire support from the rear flank. $nNote: The booster and weapons are sold separately.',NULL);
INSERT INTO "en_us" ("ID","항목","TEXTID","COLOR","DECORATION","표정","SOUND","TEXT","DESCRIPTION") VALUES (90506,'유닛설명','Unit_UnitSlotF_1',NULL,NULL,'UI',NULL,'The stalwart Boomrocker was built to withstand a frontline assault or provide fire support from the rear flank. The reinforced Boomrocker P1 is particularly advantageous in close combat. $nNote: The booster and weapons are sold separately.',NULL);
INSERT INTO "en_us" ("ID","항목","TEXTID","COLOR","DECORATION","표정","SOUND","TEXT","DESCRIPTION") VALUES (90507,'유닛설명','Unit_UnitSlotA_2',NULL,NULL,'UI',NULL,'The Stallion is maneuverable, but fragile. It has strong ranged attack capabilities, and can harass the enemy from any point on the battlefield. $nNote: The booster and weapons are sold separately.',NULL);
INSERT INTO "en_us" ("ID","항목","TEXTID","COLOR","DECORATION","표정","SOUND","TEXT","DESCRIPTION") VALUES (90508,'유닛설명','Unit_UnitSlotB_2',NULL,NULL,'UI',NULL,'The Mekhi features improved defenses while retaining good overall speed. Ideal for small battles, its superior skill usage, deceleration and energy recovery make it a favorite of veteran pilots who enjoy toying with their enemies.',NULL);
INSERT INTO "en_us" ("ID","항목","TEXTID","COLOR","DECORATION","표정","SOUND","TEXT","DESCRIPTION") VALUES (90509,'유닛설명','Unit_UnitSlotC_2',NULL,NULL,'UI',NULL,'Based on the popular Trooper model, the Stark is designed to make use of countermeasures in a variety of battle situations. It outperforms the Trooper, but only an experienced pilot can control it. $nNote: The booster and weapons are sold separately.',NULL);
INSERT INTO "en_us" ("ID","항목","TEXTID","COLOR","DECORATION","표정","SOUND","TEXT","DESCRIPTION") VALUES (90510,'유닛설명','Unit_UnitSlotD_2',NULL,NULL,'UI',NULL,'The aptly named Frontliner is built for use on the frontlines of the battlefield. The Frontliner''s design offers better defenses than a general Mechanaught. In particular the Frontliner K2 offers superior energy recovery ability and marksmanship. $nNote: The booster and weapons are sold separately.',NULL);
INSERT INTO "en_us" ("ID","항목","TEXTID","COLOR","DECORATION","표정","SOUND","TEXT","DESCRIPTION") VALUES (90511,'유닛설명','Unit_UnitSlotE_2',NULL,NULL,'UI',NULL,'The Haskell is based on the Boomrocker and has a new additional core. It shows a combat pattern clearly different from Boomrocker''s, and keeps a commanding presence on the battlefield. $nNote: Booster and weapons sold separately.',NULL);
INSERT INTO "en_us" ("ID","항목","TEXTID","COLOR","DECORATION","표정","SOUND","TEXT","DESCRIPTION") VALUES (90512,'유닛설명','Unit_UnitSlotF_2',NULL,NULL,'UI',NULL,'The aptly named Frontliner is built for use on the frontlines of the battlefield. The Frontliner''s design offers better defenses than a general Mechanaught. The Frontliner P2 offers a particularly superior defense. $nNote: The booster and weapons are sold separately.',NULL);
INSERT INTO "en_us" ("ID","항목","TEXTID","COLOR","DECORATION","표정","SOUND","TEXT","DESCRIPTION") VALUES (90513,'유닛설명','Unit_UnitSlotA_3',NULL,NULL,'UI',NULL,'The high-speed Scootwing maneuvers quickly in battle, keeping opponents off guard. Its reinforced defenses make it very effective in strategic warfare. $n:Note: Booster and weapons sold separately.',NULL);
INSERT INTO "en_us" ("ID","항목","TEXTID","COLOR","DECORATION","표정","SOUND","TEXT","DESCRIPTION") VALUES (90514,'유닛설명','Unit_UnitSlotB_3',NULL,NULL,'UI',NULL,'The Hellfire was a key tactical Mechanaught for the forces of West Palamo and is ideal for small battle situations. $nNote: The booster and weapons are sold separately.',NULL);
INSERT INTO "en_us" ("ID","항목","TEXTID","COLOR","DECORATION","표정","SOUND","TEXT","DESCRIPTION") VALUES (90515,'유닛설명','Unit_UnitSlotC_3',NULL,NULL,'UI',NULL,'Once known as the "Equalizer",  this powerful defensive model contributed significantly to the Palamo Federation’s later victories. $nNote: The booster and weapons are sold separately.',NULL);
INSERT INTO "en_us" ("ID","항목","TEXTID","COLOR","DECORATION","표정","SOUND","TEXT","DESCRIPTION") VALUES (90516,'유닛설명','Unit_UnitSlotD_3',NULL,NULL,'UI',NULL,'Eier was initially reluctant to get involved in inter-colonial conflicts.  But as the war spread, they secretly developed the Bastion, a high-performance Mechanaught on a par with any other. $nNote: The booster and weapons are sold separately.',NULL);
INSERT INTO "en_us" ("ID","항목","TEXTID","COLOR","DECORATION","표정","SOUND","TEXT","DESCRIPTION") VALUES (90517,'유닛설명','Unit_UnitSlotE_3',NULL,NULL,'UI',NULL,'This efficient Mechanaught was constructed with high maneuverability by the Galaxy Federation for special terrain warfare. It was used as a peacekeeper between the Marston and the Palamo Federation. $nNote: Booster and weapons sold separately.',NULL);
INSERT INTO "en_us" ("ID","항목","TEXTID","COLOR","DECORATION","표정","SOUND","TEXT","DESCRIPTION") VALUES (90518,'유닛설명','Unit_UnitSlotF_3',NULL,NULL,'UI',NULL,'Once known as the "Equalizer",  this powerful defensive model contributed significantly to the Palamo Federation’s later victories. The Davenstar P4 is particularly renown for its strength under fire. $nNote: The booster and weapons are sold separately.','Not decided');
INSERT INTO "en_us" ("ID","항목","TEXTID","COLOR","DECORATION","표정","SOUND","TEXT","DESCRIPTION") VALUES (90519,'유닛설명','Unit_UnitSlotA_4',NULL,NULL,'UI',NULL,'The Spazmok offers high speed and excellent abilities. As the war rages on, the Spazmok, with its superior mobility, has become more and more valuable. $nNote: Booster and weapons sold separately.',NULL);
INSERT INTO "en_us" ("ID","항목","TEXTID","COLOR","DECORATION","표정","SOUND","TEXT","DESCRIPTION") VALUES (90520,'유닛설명','Unit_UnitSlotB_4',NULL,NULL,'UI',NULL,'Zeeker is a highly-mobile, semi-lightweight Mechanaught for use only by Marston''s best pilots. $nNote: The booster and weapons are sold separately.',NULL);
INSERT INTO "en_us" ("ID","항목","TEXTID","COLOR","DECORATION","표정","SOUND","TEXT","DESCRIPTION") VALUES (90521,'유닛설명','Unit_UnitSlotC_4',NULL,NULL,'UI',NULL,'Marston''s best Mechanaught, the all-purpose Ballistika is expensive but powerful. $nNote: The booster and weapons are sold separately.',NULL);
INSERT INTO "en_us" ("ID","항목","TEXTID","COLOR","DECORATION","표정","SOUND","TEXT","DESCRIPTION") VALUES (90522,'유닛설명','Unit_UnitSlotD_4',NULL,NULL,'UI',NULL,'Once known as the "Equalizer",  this powerful defensive model contributed significantly to the Palamo Federation’s later victories. The Davenstar K4 in particular is widely known as a high-performance Mechnaught with strong Marksmanship.','Not decided');
INSERT INTO "en_us" ("ID","항목","TEXTID","COLOR","DECORATION","표정","SOUND","TEXT","DESCRIPTION") VALUES (90523,'유닛설명','Unit_UnitSlotE_4',NULL,NULL,'UI',NULL,'The Blitzker model features balanced performance, superior defenses, and precision-tuned marksmanship.  This Mechanaught has played a key role in dominating Marston''s enemies. $nNote: The booster and weapons are sold separately.',NULL);
