INSERT INTO "en_us" ("ID","항목","TEXTID","COLOR","DECORATION","표정","SOUND","TEXT","DESCRIPTION") VALUES (115001,'튜터리얼','PROTO_PARTS',NULL,NULL,'NORMAL',NULL,'You have been issued a prototype Pinkett as your Mechanaught. This is a general purpose model, ideal for learning the basics. Parts include Pinkett HD, Pinkett CS, Pinkett AR, Pinkett LE and Pinkett Booster. You may purchase upgrade parts at a later time.',NULL);
INSERT INTO "en_us" ("ID","항목","TEXTID","COLOR","DECORATION","표정","SOUND","TEXT","DESCRIPTION") VALUES (115002,'튜터리얼','PROTO_WEAPON',NULL,NULL,'NORMAL',NULL,'You have been issued two Tommy Striker-C’s, one Bloody Edge-C, and one Defender-C as your basic weapons. Please be aware that Mechanaughts must be equipped with the following items in order to enter the battlefield: head, core, arms, legs, booster and two weapons.',NULL);
INSERT INTO "en_us" ("ID","항목","TEXTID","COLOR","DECORATION","표정","SOUND","TEXT","DESCRIPTION") VALUES (115003,'튜터리얼','PROTO_SMG',NULL,NULL,'NORMAL',NULL,'The Tommy Striker-C is a great overall weapon that offers average accuracy, long range, and shot output. The only drawback is that its weight can slow down an average Mechanaught.',NULL);
INSERT INTO "en_us" ("ID","항목","TEXTID","COLOR","DECORATION","표정","SOUND","TEXT","DESCRIPTION") VALUES (115004,'튜터리얼','PROTO_BLADE',NULL,NULL,'NORMAL',NULL,'The Bloody Edge-C is a melee weapon with average range and damage. The Defender-C is a powerful shield for melee defense. These make a good combination for close combat.',NULL);
INSERT INTO "en_us" ("ID","항목","TEXTID","COLOR","DECORATION","표정","SOUND","TEXT","DESCRIPTION") VALUES (115005,'튜터리얼','PROTO_SET',NULL,NULL,'NORMAL',NULL,'A pilot can select two sets of weapons for each Mechanaught, but can also choose to use other weapon sets during a battle. You can make yourself a better fighter by selecting different weapons and adapting to each battle situation.',NULL);
INSERT INTO "en_us" ("ID","항목","TEXTID","COLOR","DECORATION","표정","SOUND","TEXT","DESCRIPTION") VALUES (115006,'튜터리얼','PROTP_END',NULL,NULL,'SMILE',NULL,'Your battle preparation is complete. Now to the Training Ground, the first battle site. Complete the tutorials in the Training Ground to receive items and credits. Good luck.',NULL);
